            "description": "This moment is yours!",
            "tag": "Personal",
            "date": "2021-11-24T18:54:55.516Z",
            "__v": 0
        }
    ]
    const [notes, setNotes] = useState(notesInitial);

    // Add a Note
    const addNote = async (title, description, tag) => {
        //API Call
        const response = await fetch(`${host}/api/notes/addnote`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'auth-token': "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjE5NDAyMGY3ZWRhMzNkYWU1YjdkNTg4In0sImlhdCI6MTYzNzkzNzE2NX0.xrqFohn6-uvsCLEPbqSJvNWVNlsUDos5SzwVPgrl3Vc"
            },
            body: JSON.stringify({title, description, tag})
        });


        //Adding a new Note
        const note = {
            "_id": "619e8a7f5d85dacc9f3b4639",
            "user": "6194020f7eda33dae5b7d588",
            "title": title,
            "description": description,
            "tag": tag,
            "date": "2021-11-24T18:54:55.516Z",
            "__v": 0
        }
        setNotes(notes.concat(note));
    }
    // Delete a Note
    const deleteNote = (id) => {
        const newNotes = notes.filter((note) => { return note._id !== id });
        setNotes(newNotes);
    }
    // Edit a Note
    const editNote = async (id, title, description, tag) => {
        //API Call
        const response = await fetch(`${host}/api/notes/updatenote/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                'auth-token': "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjE5NDAyMGY3ZWRhMzNkYWU1YjdkNTg4In0sImlhdCI6MTYzNzkzNzE2NX0.xrqFohn6-uvsCLEPbqSJvNWVNlsUDos5SzwVPgrl3Vc"
            },
            body: JSON.stringify({title, description, tag})
        });
        //const json = response.json();

        //Logic to edit in client
        for (let index = 0; index < notes.length; index++) {
            const element = notes[index];
            if (element._id === id) {
                element.title = title;
                element.description = description;
                element.tag = tag;
            }

        }
    }
    return (
        <NoteContext.Provider value={{ notes, addNote, deleteNote, editNote }}>
            {props.children}
        </NoteContext.Provider>
    )
}
export default NoteState;
